import React, { useEffect, useState } from "react";
import { AxiosResponse } from "axios";

import Header from "./components/Header";

import api from "./services/api";

import wallPapper from "./assets/wallpapper.jpg";

import "./App.css";

interface Project {
  id: string;
  title: string;
  owner: string;
}

function App() {
  const [projects, setProjects] = useState<Project[]>([]);
  const [newProject, setNewProject] = useState<Project>();

  const handleAddProject = () => {
    // setProjects((projects) => [...projects, `${Date.now()}`]);
  };

  useEffect(() => {
    api.get("/projects").then(({ data }: AxiosResponse<any>) => {
      console.log(data["projects"]);
      setProjects(Array.from(data["projects"]));
    });
  }, []);

  return (
    <div className="App">
      <Header title="Projects">
        {projects ? (
          <ul>
            {projects.map(({ id, title }) => (
              <li key={id}>{title}</li>
            ))}
          </ul>
        ) : null}

        <form
          style={{
            marginTop: "15px",
            marginBottom: "15px",
          }}
        >
          <input type="text" placeholder="Title" value={newProject?.title} />
          <input type="text" placeholder="Owner" value={newProject?.owner} />
          <button onClick={handleAddProject}>New Project</button>
        </form>
      </Header>

      <img src={wallPapper} width="450" alt="wallpapper" />
    </div>
  );
}

export default App;
