import React, { useEffect, useState } from "react";
import { AxiosResponse } from "axios";

import Header from "./components/Header";

import api from "./services/api";

import wallPapper from "./assets/wallpapper.jpg";

import "./App.css";

interface newProject {
  title: string;
  owner: string;
}
interface Project {
  id: string;
  title: string;
  owner: string;
}

function App() {
  const [projects, setProjects] = useState<Project[]>([]);
  const [newProject, setNewProject] = useState<newProject>();

  const handleAddProject = () => {
    api
      .post("/projects", { ...newProject })
      .then(({ data }: AxiosResponse<any>) => {
        setProjects((projects) => [...projects, { ...data }]);
        setNewProject({
          title: "",
          owner: "",
        });
      });
  };

  const handleNewProjectChange = (event: React.FormEvent<HTMLInputElement>) => {
    event.preventDefault();
    const target = event.target as HTMLInputElement;
    const field = target.name;
    const value = target.value || "";
    setNewProject((newProject) => {
      const title = field === "title" ? value : newProject?.title;
      const owner = field === "owner" ? value : newProject?.owner;
      return {
        title: title || "",
        owner: owner || "",
      };
    });
  };

  useEffect(() => {
    api.get("/projects").then(({ data }: AxiosResponse<any>) => {
      console.log(data["projects"]);
      setProjects(Array.from(data["projects"]));
    });
  }, []);

  return (
    <div className="App">
      <Header title="Projects">
        {projects ? (
          <ul>
            {projects.map(({ id, title, owner }) => (
              <li key={id}>
                {title} | {owner}{" "}
              </li>
            ))}
          </ul>
        ) : null}

        <form
          style={{
            marginTop: "15px",
            marginBottom: "15px",
          }}
        >
          <input
            name="title"
            type="text"
            placeholder="Title"
            value={newProject?.title}
            onChange={(e) => handleNewProjectChange(e)}
          />
          <input
            name="owner"
            type="text"
            placeholder="Owner"
            value={newProject?.owner}
            onChange={(e) => handleNewProjectChange(e)}
          />
          <button type="button" onClick={handleAddProject}>
            New Project
          </button>
        </form>
      </Header>

      <img src={wallPapper} width="450" alt="wallpapper" />
    </div>
  );
}

export default App;
