import React, { useEffect, useState } from "react";

import Header from "./components/Header";

import api from "./services/api";

import wallPapper from "./images/wallpapper.jpg";

import "./App.css";

interface ProjectsInterface {
  projects: Array<any>;
}

interface ResponseInterface {
  data: ProjectsInterface;
}

function App() {
  const [projects, setProjects] = useState<string[]>([]);

  const handleAddProject = () => {
    setProjects((projects) => [...projects, `${Date.now()}`]);
  };

  useEffect(() => {
    api.get("/projects").then((data: ResponseInterface) => {
      setProjects(data.projects);
    });
  }, []);

  return (
    <div className="App">
      <Header title="Projects">
        <ul>
          {projects.map((project) => (
            <li key={project}>{project}</li>
          ))}
        </ul>

        <button onClick={handleAddProject}>New Project</button>
      </Header>

      <img src={wallPapper} width="450" alt="wallpapper" />
    </div>
  );
}

export default App;
