import React, { createContext, useCallback, useContext, useState } from "react";

import api from "../services/api";

interface SignInCredentials {
  email: string;
  password: string;
}

interface AuthContextState {
  name: string;
  setName: Function;
  signIn({ email, password }: SignInCredentials): Promise<void>;
}
const AuthContext = createContext<AuthContextState>({} as AuthContextState);

const AuthContextProviver: React.FC = ({ children }) => {
  const [name, setName] = useState("");
  const signIn = useCallback(
    async ({ email, password }: SignInCredentials): Promise<void> => {
      const { data } = await api.post("/sessions", {
        email,
        password,
      });

      const { token } = data;
      localStorage.setItem("@GOBARBER:token", JSON.stringify(token));
    },
    []
  );

  return (
    <AuthContext.Provider value={{ name, setName, signIn }}>
      {children}
    </AuthContext.Provider>
  );
};

const useAuthContext = () => {
  const context = useContext(AuthContext);

  return {
    ...context,
  };
};

export default useAuthContext;
export { AuthContextProviver };
