import React from 'react'
import { FiAlertCircle, FiXCircle } from 'react-icons/fi'

import Toast from './Toast'

import { Container } from './styles'


import useToastContext, { ToastMessage } from '../../contexts/ToastContext'

interface ToastContainerParams {
  messages: ToastMessage[]
}
const ToastContainer: React.FC<ToastContainerParams> = ({ messages }) => {
  const { removeToast } = useToastContext()

  return (
    <Container>
      {messages.map(message => <Toast key={message.id} toast={message} />)}
    </Container>
  )
}

export default ToastContainer
