import styled from "styled-components";

import { shade } from "polished";

export const Container = styled.div`
  height: 100vh;
  display: flex;
  align-items: stretch;
`;

export const Content = styled.div`
  display: flex;
  flex-direction: column;
  margin: 0 auto;

  form {
    margin: 50px 80px;
    width: 340px;
    text-align: center;

    h1 {
      margin-bottom: 20px;
      font-size: 20px;
      text-align: left;
    }

    a {
      color: #f4ede8;
      display: block;
      text-decoration: none;
      transition: color 0.2s;

      &:hover {
        color: ${shade(0.2, "#f4ede8")};
      }
    }
  }

  img {
    margin-top: 35px;
  }
`;

export const AvatarInput = styled.div`
  margin-bottom: 32px;
  position: relative;

  img {
    width: 186px;
    height: 186px;
    border-radius: 50%;
  }

  button {
    position: absolute;
  }
`;
